import{d,h as c,o as t,c as n,a as s,f as e,b as a,g as p}from"./index-lSksSEnd.js";const _="/assets/digital_skills_components-DNXeO5sU.jpg",v="/assets/digital_skills_report_example-DV06G7eK.jpg",r={class:"page-container"},u=p('<div id="digital-skills-img" class="page-main-img"><div class="text"><div class="title"> Инструмент </div><div class="subtitle"> DIGLIT </div><div class="description"> Цифровые компетенции </div></div></div><div class="basic-container white-container"><p> Инструмент <span class="bold-text">DIGLIT</span> реализован в форме компьютерной системы тестирования с автоматизированным процессом сбора, обработки и оценки результатов и направлен на измерение цифровой грамотности 12-14 летних школьников. </p><p> Цифровая грамотность является комплексным конструктом, в состав которого входит ряд цифровых навыков, необходимых для работы в цифровой среде, что нашло отражение в определении — <span class="bold-text">способность безопасно для себя и других использовать цифровые технологии для поиска, анализа, создания, управления информацией, коммуникации и коллективной работы с целью решения задач в цифровой среде для удовлетворения личных и образовательных потребностей</span> — и обусловило подход к разработке инструмента измерения. </p></div><div class="basic-container blue-container"><div class="title"> Какие цифровые навыки измеряются? </div><div class="img-container"><img alt="digital-skills-components" src="'+_+'"></div><p> Инструмент измерения <span class="bold-text">цифровой грамотности DIGLIT</span> разработан на основе метода доказательной аргументации (Evidence-Centered Design), в основе которого заложена разработка наблюдаемых доказательств, позволяющих сделать вывод о сформированности измеряемых цифровых компетенций. </p><p> В качестве <span class="bold-text">формы задания выбран сценарный тип</span>, так как он позволяет добиться максимальной аутентичности в отличие от классических форм, моделирует ситуации, с которыми участники тестирования могут столкнуться в реальной жизни, например, планирование поездки в неизвестное место, поиск необходимой информации в интернете или создание визуализации в мультимедийной программе, создают среду, которая при этом позволяет зафиксировать поведение, соответствующее измеряемым цифровым навыкам, и в целом помогает решить задачу внутренней мотивации выполнения теста, а, следовательно, повышает степень достоверности полученных результатов. </p></div><div class="split-container"><div class="title"> Процедура<br> оценивания </div><div class="description"><p> Разработана очень подробная <span class="bold-text">процедура администрирования</span> дистанционного процесса тестирования. </p><p> Варианты теста включают <span class="bold-text">4 задания сценарного типа</span>. На выполнение непосредственно тестовых заданий отводится <span class="bold-text">60 минут</span>. Для компьютерного тестирования данной возрастной группы (школьники 12-14 лет) предусмотрены 2 перерыва по 5 минут для гимнастики глаз (20 + 20 + 20 минут с двумя перерывами). </p><p><span class="bold-text">Задания не связаны с предметными знаниями.</span> Сценарии разработаны таким образом, что их решение способно заинтересовать школьников, тем самым повышая внутреннюю мотивацию к решению задачи. </p><p><span class="bold-text">Тестовые задания предполагают использование симуляторов широкого спектра цифровых технологий</span>, в том числе браузера, облачного хранилища, мессенджеров, электронной почты, соцсетей, текстовых редакторов, программ создания презентаций и др., <span class="bold-text">полностью идентичных подобным в реальной жизни</span>. </p><p><span class="bold-text">Контекст заданий:</span> учебный (из школьной среды) и личный (задача из повседневной жизни вне школы). </p><p> Примеры заданий можно посмотреть <a class="inline-link" target="_blank" href="https://dl.hse.ru/demos">здесь</a>. </p><p><span class="bold-text">Автоматическая обработка результатов</span> с «обратной связью» респонденту сразу после окончания тестирования. </p><p><span class="bold-text">Контекстная анкета</span> разработана для выявления факторов, связанных с развитием цифровых навыков в быстро меняющейся цифровой среде. </p></div></div><div class="white-gap"></div>',5),b={class:"basic-container blue-container"},h=s("div",{class:"title"}," Обратная связь ",-1),g=s("p",null," В результате тестирования определяется общий уровень цифровой грамотности обучающихся, исходя из трех возможных: ",-1),k={class:"shifted-div"},x={key:0},m={key:1},L=s("br",null,null,-1),f={key:2,class:"shifted-div-details"},y=s("span",{class:"bold-text"},"Ученик, находящийся на развивающемся уровне:",-1),D=s("br",null,null,-1),C=s("br",null,null,-1),I=s("br",null,null,-1),V=s("br",null,null,-1),N=s("br",null,null,-1),B={key:3},G={key:4},T=s("br",null,null,-1),w={key:5,class:"shifted-div-details"},$=s("span",{class:"bold-text"},"Ученик, находящийся на базовом уровне:",-1),j=s("br",null,null,-1),E=s("br",null,null,-1),S=s("br",null,null,-1),K=s("br",null,null,-1),O=s("br",null,null,-1),U={key:6},X={key:7},q=s("br",null,null,-1),z={key:8,class:"shifted-div-details"},A=s("span",{class:"bold-text"},"Ученик, находящийся на продвинутом уровне:",-1),F=s("br",null,null,-1),H=s("br",null,null,-1),J=s("br",null,null,-1),M=s("br",null,null,-1),P=s("br",null,null,-1),Q=s("p",null," По результатам тестирования предоставляется индивидуальная обратная связь школьнику и родителям, учителю и классу, а также обобщённый отчет по школе. ",-1),R=s("p",null," Используемый метод доказательного оценивания позволяет выявить и описать пробелы в развитии цифровых компетенций школьника, над которыми следует поработать. Пример индивидуального отчета школьника для базового уровня цифровой грамотности: ",-1),W=s("div",{class:"img-container"},[s("img",{alt:"digital-skills-report-example",src:v})],-1),es=d({__name:"DigitalSkillsView",setup(Y){const l=c({developingLevel:!1,baseLevel:!1,advancedLevel:!1});return(Z,i)=>(t(),n("div",r,[u,s("div",b,[h,g,s("div",k,[e(" • "),s("span",{onClick:i[0]||(i[0]=o=>l.developingLevel=!l.developingLevel),class:"shifted-div-title"},"Развивающийся уровень"),l.developingLevel?(t(),n("span",x," ↖")):(t(),n("span",m," ↘")),L,l.developingLevel?(t(),n("div",f,[y,D,e(" • владеет основами поиска и анализа информации, но не способен выделить релевантную информацию и определить надежность источников. Плохо классифицирует информацию, не способен создать информационный продукт по заданным критериям;"),C,e(" • способен выполнить предложенную последовательность действий (алгоритм) решения практической задачи;"),I,e(" • владеет основными навыками коммуникации в цифровой среде, но, не всегда может выбрать подходящий формат представления информации для конкретной целевой аудитории, не соблюдает правила цифрового этикета;"),V,e(" • способен обнаружить наиболее очевидные угрозы, но не всегда готов защитить свои устройства; не всегда идентифицирует проблемный контент и не способен защитить личные данные;"),N,e(" • владеет минимальными техническими навыками работы с цифровыми технологиями, которые не позволяют полностью решить задачу в цифровой среде. ")])):a("",!0),e(" • "),s("span",{onClick:i[1]||(i[1]=o=>l.baseLevel=!l.baseLevel),class:"shifted-div-title"},"Базовый уровень"),l.baseLevel?(t(),n("span",B," ↖")):(t(),n("span",G," ↘")),T,l.baseLevel?(t(),n("div",w,[$,j,e(" • способен искать и анализировать информацию, выделять релевантную информацию при этом опираясь на информацию из надежных и достоверных источников. Способен классифицировать и систематизировать информацию, создавать простые информационные продукты по заданным критериям или создавать сложные информационные продукты с небольшим количеством ошибок;"),E,e(" • способен не только выполнить предложенную последовательность действий, но и составить свой алгоритм для решения задачи;"),S,e(" • способен коммуницировать в цифровой среде с пониманием основных норм и правил общения, может выбрать формат представления информации для конкретной целевой аудитории;"),K,e(" • способен идентифицировать риски, но не всегда готов защитить свои устройства и данные;"),O,e(" • владеет техническими навыками, достаточными для решения задач в цифровых средах. ")])):a("",!0),e(" • "),s("span",{onClick:i[2]||(i[2]=o=>l.advancedLevel=!l.advancedLevel),class:"shifted-div-title"},"Продвинутый уровень"),l.advancedLevel?(t(),n("span",U," ↖")):(t(),n("span",X," ↘")),q,l.advancedLevel?(t(),n("div",z,[A,F,e(" • способен как искать и анализировать информацию, так и формулировать правильное решение поставленной задачи, опираясь на надежные и достоверные источники, классифицировать и организовывать информацию для быстрого нахождения и оптимального использования при решении задач, создавать информационные продукты, соответствующие возрасту, по заданным критериям без ошибок;"),H,e(" • способен выполнить предложенную последовательность действий, составить свой алгоритм для решения задачи и провести его оптимизацию;"),J,e(" • способен выстроить эффективную коммуникацию для решения задачи с соблюдением этических и правовых норм;"),M,e(" • способен обнаружить угрозы и защитить свои устройства, избегать рисков работы в цифровой среде и полностью защитить свои конфиденциальные данные;"),P,e(" • свободно использует цифровые технологии при решении задач, решает сложные пользовательский задачи в цифровых средах и может помочь другим. ")])):a("",!0)]),Q,R,W])]))}});export{es as default};
